import React, { useState, useEffect} from 'react';
import './Register.css';
import api from '../services/api';
import logo from '../assets/logo.svg';


function handleSubmit(e){
    e.preventDefault();
}

export default function Register({ match }){
    const [name, setName] = useState(match.params.name);
    const [email, setEmail] = useState(match.params.email);
    const [phone, setPhone] = useState('');
    const [bio, setBio] = useState('');
    const [categories, setCategories] = useState([]);
    const [categoryId, setCategoryId] = useState([]);
    const [image, setImage] = useState(null);

    useEffect(() => {
        async function loadCategories(){
            const response = await api.get('/category');
            setCategories(response.data);
          
        }
        loadCategories();
    }, []);

    useEffect(() => {
        function loadCategory(){
            console.log(categoryId);
          
        }
        loadCategory();
    }, [categoryId]);

    return (
        <div className="register-container">
            <div className="image-logo">    
            <img src={logo} alt="logo" />
            <form onSubmit={handleSubmit}>
                <input type="file" name="imagem" />
                <input type="text" onChange={e => setName(e.target.value)} value={name} />
                <input type="email" onChange={e => setEmail(e.target.value)} value={email} />
                <input type="text" name="phone" onChange={e => setPhone(e.target.value)} value={phone} placeholder="Telefone" />
                <input type="tel" name="bio" onChange={e => setBio(e.target.value)}  value={bio} placeholder="Bio" />
                { categories.length > 0 ? (
                    <label> Categorias <br/>
                     { categories.map(category => (
                        <label key={category._id} >{category.name}
                        <input type="checkbox" name={category.name} onChange={ e => setCategoryId([e.target.value])} value={category._id}  id="category" /> 
                        </label> 
                    ))}
                    </label>
                   ) : '' }
                
                <button type="submit" >Enviar</button>
            </form>
            </div>

        </div>
    )
}
